package uebung_2_2;

public class BinomischeFormel {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double a = 8;
		double b = 3;
		
		double binomischeFormel = (a*a) + (2*a*b) + (b*b);
		
		System.out.println("Das Ergebnis ist: "+binomischeFormel );
		
				
		
	}

}
